document.addEventListener(&quot;DOMContentLoaded&quot;, () =&gt; {
    const articleId = new URLSearchParams(window.location.search).get(&quot;id&quot;);
    const articleContainer = document.getElementById(&quot;article-container&quot;);

    fetch(`api/load_article.php?id=${articleId}`)
        .then(response =&gt; response.json())
        .then(article =&gt; {
            if (article.error) {
                articleContainer.innerHTML = `&lt;p&gt;${article.error}&lt;/p&gt;`;
                return;
            }

            articleContainer.innerHTML = `
                &lt;h1&gt;${article.title}&lt;/h1&gt;
                &lt;p&gt;&lt;strong&gt;Autor:&lt;/strong&gt; &lt;img src=&quot;author_images/${article.authorImage}&quot; alt=&quot;${article.author}&quot; class=&quot;author-image&quot;&gt; ${article.author}&lt;/p&gt;
                &lt;p&gt;${article.content}&lt;/p&gt;
                &lt;img src=&quot;articles/${article.image}&quot; alt=&quot;${article.title}&quot;&gt;
                &lt;p&gt;&lt;strong&gt;Ver√∂ffentlicht am:&lt;/strong&gt; ${article.date}&lt;/p&gt;
                &lt;button onclick=&quot;updateArticleLike(&#039;like&#039;)&quot;&gt;Gef√§llt mir&lt;/button&gt;
                &lt;button onclick=&quot;updateArticleLike(&#039;dislike&#039;)&quot;&gt;Gef√§llt mir nicht&lt;/button&gt;
                &lt;span id=&quot;article-likes&quot;&gt;üëç ${article.likes || 0}&lt;/span&gt;
                &lt;span id=&quot;article-dislikes&quot;&gt;üëé ${article.dislikes || 0}&lt;/span&gt;
            `;

            // Load comments after the article is loaded
            loadComments();
        })
        .catch(error =&gt; {
            articleContainer.innerHTML = `&lt;p&gt;Fehler beim Laden des Artikels: ${error.message}&lt;/p&gt;`;
        });
});

async function updateArticleLike(type) {
    const articleId = new URLSearchParams(window.location.search).get(&quot;id&quot;);

    const response = await fetch(`api/update_article_like.php?article_id=${articleId}`, {
        method: &#039;POST&#039;,
        headers: { &#039;Content-Type&#039;: &#039;application/json&#039; },
        body: JSON.stringify({ type: type })
    });

    const result = await response.json();

    if (result.success) {
        // Reload the article to update the like/dislike counts
        const articleContainer = document.getElementById(&quot;article-container&quot;);
        fetch(`api/load_article.php?id=${articleId}`)
            .then(response =&gt; response.json())
            .then(article =&gt; {
                if (article.error) {
                    articleContainer.innerHTML = `&lt;p&gt;${article.error}&lt;/p&gt;`;
                    return;
                }

                articleContainer.innerHTML = `
                    &lt;h1&gt;${article.title}&lt;/h1&gt;
                    &lt;p&gt;&lt;strong&gt;Autor:&lt;/strong&gt; &lt;img src=&quot;author_images/${article.authorImage}&quot; alt=&quot;${article.author}&quot; class=&quot;author-image&quot;&gt; ${article.author}&lt;/p&gt;
                    &lt;p&gt;${article.content}&lt;/p&gt;
                    &lt;img src=&quot;articles/${article.image}&quot; alt=&quot;${article.title}&quot;&gt;
                    &lt;p&gt;&lt;strong&gt;Ver√∂ffentlicht am:&lt;/strong&gt; ${article.date}&lt;/p&gt;
                    &lt;button onclick=&quot;updateArticleLike(&#039;like&#039;)&quot;&gt;Gef√§llt mir&lt;/button&gt;
                    &lt;button onclick=&quot;updateArticleLike(&#039;dislike&#039;)&quot;&gt;Gef√§llt mir nicht&lt;/button&gt;
                    &lt;span id=&quot;article-likes&quot;&gt;üëç ${article.likes || 0}&lt;/span&gt;
                    &lt;span id=&quot;article-dislikes&quot;&gt;üëé ${article.dislikes || 0}&lt;/span&gt;
                `;

                // Load comments after the article is loaded
                loadComments();
            })
            .catch(error =&gt; {
                articleContainer.innerHTML = `&lt;p&gt;Fehler beim Laden des Artikels: ${error.message}&lt;/p&gt;`;
            });
    } else {
        alert(&#039;Fehler beim Aktualisieren: &#039; + result.message);
    }
}
